mapper.select:

WHERE
art.id = #{id}
GROUP BY art.id


WHERE
art_alias = #{alias}
GROUP BY art.id

SELECT COUNT(art.id) FROM blog_article art LEFT OUTER JOIN blog_space sp ON art.space_id = sp.id
art.space_id = #{space.id}
AND art.isPrivate = false AND sp.is_private = false
AND art.space_id IN
#{id}
AND art.pubDate BETWEEN #{begin} AND #{end}
AND art.art_status = #{status,javaType=ArticleStatus,typeHandler=EnumOrdinalTypeHandler}
AND art.art_from = #{from,javaType=ArticleFrom,typeHandler=EnumOrdinalTypeHandler}
AND art.art_lock IS NULL AND sp.space_lock IS NULL
AND art.id IN (SELECT article_id FROM blog_article_tag bat WHERE bat.tag_id = #{tagId})

art.space_id = #{space.id}
AND art.isPrivate = false AND sp.is_private = false
AND art.space_id IN
#{id}
AND art.pubDate BETWEEN #{begin} AND #{end}
AND art.art_status = #{status,javaType=ArticleStatus,typeHandler=EnumOrdinalTypeHandler}
AND art.art_from = #{from,javaType=ArticleFrom,typeHandler=EnumOrdinalTypeHandler}
AND art.art_lock IS NULL AND sp.space_lock IS NULL
AND art.id IN (SELECT article_id FROM blog_article_tag bat WHERE bat.tag_id = #{tagId})
GROUP BY art.id ORDER BY
art_level DESC,
art.hits DESC,
art.lastModifyDate DESC,art.pubDate DESC,
art.pubDate DESC,
art.pubDate DESC,
art.id DESC
LIMIT #{offset},#{pageSize}


WHERE art.id IN
#{id}
GROUP BY art.id

mapper.update:
UPDATE blog_article SET hits = #{hits} WHERE
id =
#{id}

mapper.select:
SELECT COALESCE(SUM(hits),0) AS totalHits, COUNT(art.id) AS totalArticles, MAX(lastModifyDate) AS lastModifyDate, MAX(pubDate) AS lastPubDate FROM blog_article art LEFT OUTER JOIN blog_space sp ON art.space_id = sp.id WHERE art.art_status = 0
AND art.space_id = #{space.id}
AND art.isPrivate = false AND sp.is_private = false

select substr(pubDate,1,10) as pubDate from blog_article art LEFT OUTER JOIN blog_space sp
on art.space_id = sp.id
where art_status = 0

	AND art.space_id = #{space.id}


	AND art.isPrivate = false
	AND sp.is_private = false


	AND substr(pubDate,1,10)  <  #{ymd}

GROUP BY substr(pubDate,1,10)
order by pubDate desc

	LIMIT #{offset},#{pageSize}

select count(distinct(substr(pubDate,1,10))) from blog_article art LEFT OUTER JOIN blog_space sp on art.space_id = sp.id where art_status = 0
AND art.space_id = #{space.id}
AND art.isPrivate = false AND sp.is_private = false
AND substr(pubDate,1,10) < #{ymd}
