mapper.select:

WHERE
art.id = #{id}
GROUP BY art.id


WHERE art.id IN
#{id}
GROUP BY art.id


WHERE
art_alias = #{alias}
GROUP BY art.id

SELECT COUNT(art.id) FROM blog_article art LEFT OUTER JOIN blog_space sp ON art.space_id = sp.id
art.space_id = #{space.id}
AND art.isPrivate = false AND sp.is_private = false
AND art.space_id IN
#{id}
AND art.pubDate BETWEEN #{begin} AND #{end}
AND art.art_status = #{status,javaType=ArticleStatus,typeHandler=EnumOrdinalTypeHandler}
AND art.art_from = #{from,javaType=ArticleFrom,typeHandler=EnumOrdinalTypeHandler}
AND art.art_lock IS NULL AND sp.space_lock IS NULL
AND art.id IN (SELECT article_id FROM blog_article_tag bat WHERE bat.tag_id = #{tagId})

SELECT art.id AS art_id, art.pubDate, art.lastModifyDate, art.title, art.isPrivate, art.hits, art.summary, art.art_level, art.art_status, art.editor, art.art_from, art.space_id, art.art_lock, art.art_alias, art.feature_image, sp.space_alias, space_lock, sp.is_private AS space_is_private, sp.space_name, GROUP_CONCAT(bt.tag_name) AS tags FROM blog_article art LEFT OUTER JOIN blog_space sp ON art.space_id = sp.id LEFT OUTER JOIN blog_article_tag bat ON bat.article_id = art.id LEFT OUTER JOIN blog_tag bt ON bat.tag_id = bt.id
art.space_id = #{space.id}
AND art.isPrivate = false AND sp.is_private = false
AND art.space_id IN
#{id}
AND art.pubDate BETWEEN #{begin} AND #{end}
AND art.art_status = #{status,javaType=ArticleStatus,typeHandler=EnumOrdinalTypeHandler}
AND art.art_from = #{from,javaType=ArticleFrom,typeHandler=EnumOrdinalTypeHandler}
AND art.art_lock IS NULL AND sp.space_lock IS NULL
AND art.id IN (SELECT article_id FROM blog_article_tag bat WHERE bat.tag_id = #{tagId})
GROUP BY art.id ORDER BY
art_level DESC,
art.hits DESC,
art.lastModifyDate DESC,art.pubDate DESC,
art.pubDate DESC,
art.pubDate DESC,
art.id DESC
LIMIT #{offset},#{pageSize}

mapper.update:
