id =
#{id}

mapper.select:
SELECT art.id AS art_id, art.art_alias, art.title, art.feature_image, art.space_id, sp.space_alias, sp.space_name FROM blog_article art LEFT OUTER JOIN blog_space sp ON art.space_id = sp.id WHERE art.art_status = 0 AND art.pubDate < #{article.pubDate}
AND art.space_id = #{article.space.id}
AND art.isPrivate = false AND sp.is_private = false
AND art.art_lock IS NULL AND sp.space_lock IS NULL
ORDER BY art.pubDate DESC,art.id DESC LIMIT 0,1

SELECT art.id AS art_id, art.art_alias, art.title, art.space_id, art.feature_image, sp.space_alias, sp.space_name FROM blog_article art LEFT OUTER JOIN blog_space sp ON art.space_id = sp.id WHERE art.art_status = 0 AND art.pubDate > #{article.pubDate}
AND art.space_id = #{article.space.id}
AND art.isPrivate = false AND sp.is_private = false
AND art.art_lock IS NULL AND sp.space_lock IS NULL
ORDER BY art.pubDate ASC,art.id ASC LIMIT 0,1

SELECT COALESCE(SUM(hits),0) AS totalHits, COUNT(art.id) AS totalArticles, MAX(lastModifyDate) AS lastModifyDate, MAX(pubDate) AS lastPubDate FROM blog_article art LEFT OUTER JOIN blog_space sp ON art.space_id = sp.id WHERE art.art_status = 0
AND art.space_id = #{space.id}
AND art.isPrivate = false AND sp.is_private = false
