mapper.select:
SELECT id, fragment_name, fragment_description, fragment_tpl, fragment_create_date, space_id, is_global, is_callable, is_del FROM blog_fragment_user WHERE fragment_name = #{name} AND is_global = FALSE
AND space_id IS NULL
AND space_id = #{space.id}

SELECT bfu.id, fragment_name, fragment_description, fragment_tpl, fragment_create_date, space_id, space_name, is_global, is_callable FROM blog_fragment_user bfu LEFT OUTER JOIN blog_space bs ON bfu.space_id = bs.id WHERE is_del = false
AND fragment_name LIKE CONCAT('%', #{name},'%')
AND space_id IS NULL
AND space_id = #{space.id}
AND is_global = #{global}
AND is_callable = #{callable}
ORDER BY fragment_create_date DESC LIMIT #{offset},#{pageSize}

SELECT COUNT(id) FROM blog_fragment_user WHERE is_del = false
AND fragment_name LIKE CONCAT('%', #{name},'%')
AND space_id IS NULL
AND space_id = #{space.id}
AND is_global = #{global}
AND is_callable = #{callable}

mapper.update:
UPDATE blog_fragment_user
fragment_name = #{name},
fragment_description = #{description},
fragment_tpl = #{tpl},
is_del = #{del}, space_id = #{space.id}, is_global = #{global}, is_callable = #{callable}
WHERE id = #{id}

mapper.select:
