SELECT id,news_content,news_write,news_update,news_private,news_allowComment,news_hits FROM blog_news


		news_write BETWEEN #{begin} AND #{end}


		AND news_private = false




		ORDER BY news_write


		ORDER BY news_write DESC,id DESC


LIMIT #{offset},#{pageSize}

SELECT COUNT(id)  FROM blog_news


		news_write BETWEEN #{begin} AND #{end}


		AND news_private = false

SELECT id,news_content,news_write,news_update,news_private,news_allowComment,news_hits FROM blog_news


		news_private = #{queryPrivate}


ORDER BY news_write DESC,id DESC 
LIMIT 0,#{limit}

SELECT id,news_write,news_update,news_private,news_allowComment,news_hits FROM blog_news WHERE news_write < #{news.write}
AND news_private = false
ORDER BY news_write DESC,id DESC LIMIT 0,1

SELECT id,news_write,news_update,news_private,news_allowComment,news_hits FROM blog_news WHERE news_write > #{news.write}
AND news_private = false
ORDER BY news_write ASC,id ASC LIMIT 0,1
