SELECT bpu.id,space_id,page_tpl,page_name,page_description,page_create_date,space_name,space_alias,page_alias,page_allowComment,page_spaceGlobal,is_enable 
FROM blog_page_user bpu LEFT OUTER JOIN blog_space bs ON bpu.space_id = bs.id 
