SELECT bpu.id,space_id,page_tpl,page_name,page_description,page_create_date,space_name,space_alias,page_alias,page_allowComment,page_spaceGlobal,is_enable FROM blog_page_user bpu LEFT OUTER JOIN blog_space bs ON bpu.space_id = bs.id
bpu.space_id IS NULL
bpu.space_id = #{space.id}
AND (page_name LIKE CONCAT('%', #{query},'%') OR page_alias LIKE CONCAT('%', #{query},'%'))
ORDER BY page_create_date DESC,bpu.id DESC LIMIT #{offset},#{pageSize}

