SELECT bpu.id,space_id,page_tpl,page_name,page_description,page_create_date,space_name,space_alias,page_alias,page_allowComment,page_spaceGlobal FROM blog_page_user bpu LEFT OUTER JOIN blog_space bs ON bpu.space_id = bs.id
bpu.space_id IS NULL
bpu.space_id = #{space.id}
AND page_name LIKE CONCAT('%', #{name},'%')
AND page_alias LIKE CONCAT('%', #{alias},'%')
ORDER BY page_create_date DESC,bpu.id DESC LIMIT #{offset},#{pageSize}

